mapper.select:
SELECT RM, CNO, TITLE, CONTENTS, FILENAME, VIEWCOUNT, REGDATE, CATEGORY, INFOCATEGORY, UNO, LCOUNT, RCOUNT FROM ( SELECT ROWNUM RM, CNO, TITLE, CONTENTS, FILENAME, VIEWCOUNT, REGDATE, CATEGORY, INFOCATEGORY, UNO, LCOUNT, RCOUNT FROM( SELECT A.C_NO AS CNO, A.C_TITLE AS TITLE, A.C_CONTENTS AS CONTENTS, A.C_FILENAME AS FILENAME, A.C_VIEW AS VIEWCOUNT, TO_CHAR(A.C_REGDATE, 'YYYY/MM/DD HH24:MI')REGDATE, A.C_CATEGORY AS CATEGORY, A.C_INFOCATEGORY AS INFOCATEGORY, A.U_ID AS UNO, NVL(B.L_COUNT, 0) AS LCOUNT, NVL(B.R_COUNT,0)RCOUNT FROM WEBBOARD A LEFT JOIN ( SELECT A.C_NO, SUM(A.L_COUNT)L_COUNT, NVL(C.R_NO,0)R_COUNT FROM WEBLIKE A FULL JOIN ( SELECT C_NO, COUNT(R_NO)R_NO FROM BOARDREPLY GROUP BY C_NO) C ON A.C_NO = C.C_NO GROUP BY A.C_NO, C.R_NO) B ON A.C_NO = B.C_NO WHERE C_CATEGORY=#{category} AND C_TITLE LIKE '%'||#{search}||'%' ORDER BY
CNO DESC LCOUNT DESC RCOUNT DESC VIEWCOUNT DESC CATEGORY DESC
) A ) WHERE RM BETWEEN #{pageVo.startList} AND #{pageVo.listSize}

mapper.update:
UPDATE WEBBOARD SET C_TITLE=#{title}, C_CONTENTS=#{contents}, C_CATEGORY = #{category}, C_INFOCATEGORY = #{infoCategory}
	, C_FILENAME = #{fileName}

WHERE C_NO = #{cno}

mapper.select:
SELECT L_COUNT
FROM WEBLIKE
WHERE C_NO = #{cno} AND U_ID = #{id}

SELECT L_NO AS LNO
FROM WEBLIKE
WHERE C_NO = #{cno} AND U_ID = #{uno}

SELECT L_COUNT AS LCOUNT
FROM WEBLIKE
WHERE C_NO = #{cno} AND U_ID = #{uno}
